<template>  <div class="container">    <a-form :model="formState" name="normal_login" class="login-form" @finish="onFinish">      <a-form-item label="Username" name="username" :rules="[{ required: true, message: '请输入用户名' }]">        <a-input v-model:value="formState.username">          <template #prefix>            <UserOutlined class="site-form-item-icon" />          </template>        </a-input>      </a-form-item>      <a-form-item label="Password" name="password" :rules="[{ required: true, message: '请输入密码' }]">        <a-input-password v-model:value="formState.password">          <template #prefix>            <LockOutlined class="site-form-item-icon" />          </template>        </a-input-password>      </a-form-item>      <a-form-item>        <a-button type="primary" html-type="submit" class="login-form-button"> 登录 </a-button>      </a-form-item>    </a-form>  </div></template><script setup>  import { reactive } from 'vue'  import { UserOutlined, LockOutlined } from '@ant-design/icons-vue'  import { useRouter } from 'vue-router'  import request from '../../utils/request.ts'  const router = useRouter()  const formState = reactive({    username: '',    password: '',    remember: true  })  const onFinish = (values) => {    if (values) {      const randomStr = new Date().getTime()      request('post', `/admin/manager/login?randomStr=${randomStr}`, { ...formState, code: '1234', randomStr }).then(        (res) => {          if (res.code === '200') {            localStorage.token = res.data.access_token            localStorage.username = res.data.username            router.replace('/home')          }        }      )    }  }</script><style lang="less" scoped>  .container {    width: 100%;    height: 100vh;    background-image: url(../../assets/images/);  }</style>